INSERT INTO t_artma01 (article_dt, article_seq, article_title, article_sub_title, article_content, inp_id, inp_dttm, upd_id, upd_dttm)
     SELECT /* insertArticle */
            TO_CHAR(now(), 'YYYYMMDD')
          , (SELECT COALESCE(MAX(T0.article_seq), 0) + 1 FROM t_artma01 T0 WHERE T0.article_dt=TO_CHAR(now(), 'YYYYMMDD'))
          , #{title}
          , #{subTitle}
          , #{content}
          , #{userId}
          , now()
          , #{userId}
          , now()
